import { useEffect, useState } from 'react';

export default function DynamicTable({ config }) {
  const [items, setItems] = useState([]);

  useEffect(() => {
    fetch(config.apiBase)
      .then(res => res.json())
      .then(data => setItems(Array.isArray(data) ? data : []))
      .catch(err => console.error(err));
  }, [config.apiBase]);

  return (
    <table className="w-full border mt-4">
      <thead className="bg-gray-200">
        <tr>
          {config.fields.map(f => (
            <th key={f.name}>{f.label}</th>
          ))}
          {config.permissions.edit || config.permissions.delete ? <th>Action</th> : null}
        </tr>
      </thead>
      <tbody>
        {items.map(item => (
          <tr key={item.id}>
            {config.fields.map(f => (
              <td key={f.name}>{item[f.name]}</td>
            ))}
            {(config.permissions.edit || config.permissions.delete) && (
              <td>
                {config.permissions.edit && <button className="mr-2 text-blue-500">Edit</button>}
                {config.permissions.delete && <button className="text-red-500">Delete</button>}
              </td>
            )}
          </tr>
        ))}
      </tbody>
    </table>
  );
}
