import PageRenderer from './PageRenderer';

export default function PageManagement() {
  const [config, setConfig] = useState(null);

  useEffect(() => {
    // Lấy từ localStorage hoặc API
    const savedConfig = localStorage.getItem('page_config');
    if (savedConfig) {
      setConfig(JSON.parse(savedConfig));
    } else {
      // fallback config mặc định
      setConfig({
        title: 'Product Management',
        apiBase: '/api/product',
        fields: [
          { name: 'name', label: 'Product Name', type: 'text' },
          { name: 'price', label: 'Price', type: 'number' }
        ],
        permissions: ['view_product', 'add_product', 'edit_product', 'delete_product']
      });
    }
  }, []);

  if (!config) return <p>Loading config...</p>;

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">{config.title}</h1>
      <PageRenderer config={config} />
    </div>
  );
}
