import React, { useEffect, useState } from 'react';
import DynamicTable from './components/DynamicTable';
import DynamicForm from './components/DynamicForm';
import { fetchWithAuth } from './hooks/useApi';

const PageRenderer = () => {
  const [items, setItems] = useState([]);
  const [page, setPage] = useState(1);
  const pageSize = 5;

  const fetchData = async () => {
    const data = await fetchWithAuth('/api/product');
    setItems(data);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleAdd = async (item) => {
    await fetchWithAuth('/api/product', {
      method: 'POST',
      body: JSON.stringify(item),
    });
    fetchData();
  };

  const handleEdit = async (item) => {
    await fetchWithAuth(`/api/product/${item.id}`, {
      method: 'PUT',
      body: JSON.stringify(item),
    });
    fetchData();
  };

  const handleDelete = async (id) => {
    await fetchWithAuth(`/api/product/${id}`, {
      method: 'DELETE' });
    fetchData();
  };

  const currentItems = items.slice((page - 1) * pageSize, page * pageSize);

  return (
    <div>
      <h2>Quản lý sản phẩm</h2>
      <DynamicTable items={currentItems} page={page} pageSize={pageSize} onEdit={handleEdit} onDelete={handleDelete} />
      <DynamicForm onSubmit={handleAdd} />
    </div>
  );
};

export default PageRenderer;
