import React, { useState } from 'react';
import Button from './DynamicButton';

export default function DynamicForm({ fields, onSubmit }) {
  const [form, setForm] = useState({});

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(form);
    setForm({});
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginTop: '20px' }}>
      {fields.map((f) => {
        const Component = components[f.component]
        return (
          <div key={f.name} style={{ marginBottom: '10px' }}>
            <label>{f.label}</label>
            <Component
              name={f.name}
              value={formData[f.name] || ""}
              onChange={(e) => setFormData({ ...formData, [f.name]: e.target.value })}
            />
          </div>
        )
      })}
      <Button color="green" type="submit">Add</Button>
    </form>
  );
}

const components = {
  text: () => <input />,
  textArea: (props) => <textarea {...props}/>
}
