import React, { useEffect, useState } from 'react';
import DynamicForm from './DynamicForm';
import DynamicTable from './DynamicTable';
import { hasPermission } from '../../components/permission';

const PageRenderer = ({ config }) => {
  const [items, setItems] = useState([]);
  const [editingItem, setEditingItem] = useState(null);

  const fetchData = async () => {
    try {
      const res = await fetch(config.apiUrl);
      const data = await res.json();
      setItems(data);
    } catch (err) {
      console.error('Failed to fetch:', err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleAdd = async (newItem) => {
    const res = await fetch(config.apiUrl, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newItem),
    });
    if (res.ok) fetchData();
  };

  const handleEdit = async (updatedItem) => {
    const res = await fetch(`${config.apiUrl}/${updatedItem.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updatedItem),
    });
    if (res.ok) {
      setEditingItem(null);
      fetchData();
    }
  };

  const handleDelete = async (id) => {
    const res = await fetch(`${config.apiUrl}/${id}`, { method: 'DELETE' });
    if (res.ok) fetchData();
  };

  return (
    <div>
      {hasPermission(config.permissions.view) && (
        <DynamicTable
          items={items}
          fields={config.fields}
          onDelete={hasPermission(config.permissions.delete) ? handleDelete : null}
          onEdit={hasPermission(config.permissions.edit) ? setEditingItem : null}
        />
      )}
      {(hasPermission(config.permissions.add) || editingItem) && (
        <DynamicForm
          fields={config.fields}
          onSubmit={editingItem ? handleEdit : handleAdd}
          initialData={editingItem}
        />
      )}
    </div>
  );
};

export default PageRenderer;
